import { ref, onValue } from "firebase/database";
import { useEffect, useState } from "react";
import { database } from "../../config/Firebase/firebase";
import ClipLoader from "react-spinners/ClipLoader";

const Header = () => {
  const [header, setHeader] = useState({});
  const [loading, setLoading] = useState(true); // Menambahkan state untuk loading

  useEffect(() => {
    const headerRef = ref(database, "header/");
    onValue(headerRef, (snapshot) => {
      const data = snapshot.val();
      setHeader(data);
      setLoading(false); // Menandakan data sudah dimuat
    });
  }, []);

  if (loading) {
    return (
      <div
        className="d-flex justify-content-center align-items-center"
        style={{ height: "100vh" }}
      >
        <ClipLoader size={50} color={"#0000ff"} loading={loading} />
      </div>
    );
  }

  return (
    <div className="container-fluid header bg-primary p-0 mb-5">
      <div className="row g-0 align-items-center flex-column-reverse flex-lg-row">
        <div className="col-lg-6 p-5 wow fadeIn" data-wow-delay="0.1s">
          <h1 className="display-4 text-white mb-5">{header.title}</h1>
        </div>
        <div className="col-lg-6 wow fadeIn" data-wow-delay="0.5s">
          <div className="owl-carousel header-carousel">
            <div className="owl-carousel-item position-relative">
              <img
                className="img-fluid"
                src={`data:image/jpeg;base64, ${header.img}`}
                alt="Header Image"
              />
              <div className="owl-carousel-text">
                <h1 className="display-1 text-white mb-0">{header.h1img}</h1>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
