import React, { useEffect, useState } from "react";
import { get, ref } from "firebase/database";

const Header = () => {
  const [headerData, setHeaderData] = useState({
    h1img: "", // Gambar pertama untuk h1
    title: "", // Judul
  });

  useEffect(() => {
    const headerRef = ref(database, "header"); // Mendapatkan data dari node "header" di Firebase
    get(headerRef)
      .then((snapshot) => {
        if (snapshot.exists()) {
          setHeaderData(snapshot.val()); // Menyimpan data ke state headerData
        } else {
          console.error("No data available");
        }
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  }, []);

  return (
    <div className="container-fluid header bg-primary p-0 mb-5">
      <div className="row g-0 align-items-center flex-column-reverse flex-lg-row">
        <div className="col-lg-6 p-5 wow fadeIn" data-wow-delay="0.1s">
          <h1 className="display-4 text-white mb-5">{headerData.title}</h1>
        </div>
        <div className="col-lg-6 wow fadeIn" data-wow-delay="0.5s">
          <div className="owl-carousel header-carousel">
            <div className="owl-carousel-item position-relative">
              {/* Pastikan Anda mengganti src img dengan data yang sesuai */}
              <img
                className="img-fluid"
                src={headerData.h1img || "img/carousel-2.jpg"} // Jika h1img kosong, gunakan gambar default
                alt="Carousel"
              />
              <div className="owl-carousel-text">
                <h1 className="display-1 text-white mb-0">
                  {headerData.h1img ? "Image Loaded" : "Loading Image..."}{" "}
                  {/* Tampilkan pesan jika h1img kosong */}
                </h1>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
