import { getDatabase, ref, onValue } from "firebase/database";
import { useEffect, useState } from "react";

const Header = () => {
  const [header, setHeader] = useState({});

  useEffect(() => {
    const db = getDatabase();
    const headerRef = ref(db, "header/");
    onValue(headerRef, (snapshot) => {
      const data = snapshot.val();
      setHeader(data);
    });
  }, []);

  return (
    <div className="container-fluid header bg-primary p-0 mb-5">
      <div className="row g-0 align-items-center flex-column-reverse flex-lg-row">
        <div className="col-lg-6 p-5 wow fadeIn" data-wow-delay="0.1s">
          <h1 className="display-4 text-white mb-5">{header.title}</h1>
        </div>
        <div className="col-lg-6 wow fadeIn" data-wow-delay="0.5s">
          <div className="owl-carousel header-carousel">
            <div className="owl-carousel-item position-relative">
              {/* Pastikan Anda mengganti src img dengan data yang sesuai */}
              <img
                className="img-fluid"
                src={header.h1img || "img/carousel-2.jpg"} // Jika h1img kosong, gunakan gambar default
                alt="Carousel"
              />
              <div className="owl-carousel-text">
                <h1 className="display-1 text-white mb-0">
                  {header.h1img ? "Image Loaded" : "Loading Image..."}{" "}
                  {/* Tampilkan pesan jika h1img kosong */}
                </h1>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
